userDefinedLinkHintCss:
:root {
  --font-size: 15;
  --font-weight: Semibold;
  --font: SF Mono;
  --padding: 5px;
  --shadow: 0 2px 4px rgba(0, 0, 0, 0.50), 0 1px 2px rgba(0, 0, 0, 0.24);

  /* ---------- MONOKAI ---------- */
  --fg: #ffffff;
  --bg: #0f0f0d;
  --bg-dark: #1D1E19;
  --border: #2D2E2E;
  --main-fg: #3281ea;
  --accent-fg: #F92660 ;

  /* Unused Alternate Colors */
  /* --red: #E74C3C; */
  /* --orange: #FD971F; */
  /* --blue: #268BD2; */
  /* --violet: #9C91E4; */
  /* --cyan: #66D9EF; */

}

/*****************************************************************************/
/*                                    CSS                                    */
/*****************************************************************************/

/* -------- HINTS -------- */
#vimiumHintMarkerContainer div.internalVimiumHintMarker, #vimiumHintMarkerContainer div.vimiumHintMarker {
  background: var(--bg);
  border: 1px solid var(--border);
  box-shadow: var(--shadow);
  padding: 3px 4px;
  border-radius:10px;
}

#vimiumHintMarkerContainer div span {
  color: var(--main-fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
  text-shadow: none;
}

#vimiumHintMarkerContainer div > .matchingCharacter {
  opacity: 0.3;
}

#vimiumHintMarkerContainer div > .matchingCharacter ~ span {
  color: var(--main-fg);
}

/* -------- VOMNIBAR -------- */
#vomnibar {
  animation: show 100ms cubic-bezier(0, 0, 0.9, 0.1) forwards;
  background: var(--bg);
  border: none;
  box-shadow: var(--shadow);
  border-radius: 10px 10px;
  padding: .4rem .4rem; 
}

/* Animate Slide in */
@keyframes show {
  0% {
    opacity: 0;
    transform: translateY(100px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

#vomnibar .vomnibarSearchArea,
#vomnibar input {
  background: transparent;
  border: none;
  box-shadow: none;
  color: var(--fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);

}

#vomnibar .vomnibarSearchArea {
  padding: var(--padding) 30px;
}

#vomnibar input {
  padding: var(--padding)}


#vomnibar ul {
  background: var(--bg);
  border-top: 1px solid var(--border);
  margin: 0;
  padding: var(--padding);
   border-radius:10px;
}

#vomnibar li {
  border-bottom: 1px solid var(--border);
  padding: var(--padding);
   border-radius:10px;
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
  padding: var(--padding) 0;
}

#vomnibar li .vomnibarSource {
  color: var(--main-fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: var(--main-fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
}

#vomnibar li .vomnibarUrl {
  color: var(--fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
}

#vomnibar li .vomnibarMatch {
  color: var(--fg);
  font-weight: normal;
  border-radius:10px;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: var(--main-fg);
 border-radius:10px;
}

#vomnibar li.vomnibarSelected {
  background-color: var(--border);
 border-radius:10px;
}

/* -------- HUD -------- */
div.vimiumHUD {
  background: var(--bg);
  border: 1px solid var(--border);
  box-shadow: var(--shadow);
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: var(--fg);
  font-family: var(--font);
  font-size: var(--font-size);
  font-weight: var(--font-weight);
}

div.vimiumHUD .hud-find {
  background-color: transparent;
  border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background-color: transparent;
}
